import React, { Component, useEffect } from 'react';
import {
    SafeAreaView,
    StyleSheet,
    ScrollView,
    View,
    Text,
    Image,
    TouchableOpacity,
    FlatList,
    Dimensions
} from 'react-native';
import { AppRoute } from '../navigation/app-routes';
import { Colors } from 'react-native/Libraries/NewAppScreen';
import { HOST_URL } from '../utils/AppConst';
import RtspVideoPlayer from "../components/RtspVideoPlayer";
import AsyncStorage from '@react-native-community/async-storage';
import { AuthContext } from '../navigation/AppNavigator';
const WIDTH_SCREEN = Dimensions.get('window').width;
const THUMBNAILS = 'data:image/png;base64,';

export default function HomeScreen({ props }) {

    const [cameras, setCameras] = React.useState([])
    const { signOut } = React.useContext(AuthContext);

    // goToSignIn = () => {
    //     console.log('i am here')
    //     signOut();
    //     // let { navigation } = this.props;
    //     // navigation && navigation.pop();
    // }

    useEffect(async () => {
        let token = '';
        try {
            token = await AsyncStorage.getItem('userToken');
        } catch (e) {
            // Restoring token failed
        }
        try {
            const response = await fetch("http://165.22.98.234/camera/listcam", {
                method: 'GET',
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            })

            // .then(response => console.log('response', response))
            // console.log('res', response)
            // .catch(e => console.warn('error'))
            let { statusCode, message } = response;
            if (statusCode === 403 || statusCode === 404) {
                // signOut();
            } else {
                let { result } = response || {};
                setCameras(result)
            }
        } catch (error) { console.log('error', error); }

        // console.log(response)

    }, [])

    const _onPress = (camera) => {
        const { navigation } = props || {};
        navigation.navigate(
            'CameraDetails',
            { camera: camera }
        )
    }

    const renderEmpty = () => {
        return (
            <View><Text>no camera</Text></View>
        )
    }

    if (!Array.isArray(cameras) || cameras.length == 0) return renderEmpty();
    return (
        <View style={styles.container}>
            <FlatList
                style={{ flex: 1, width: WIDTH_SCREEN }}
                data={cameras}
                numColumns={2}
                keyExtractor={(item, index) => index.toString()}
                renderItem={({ item, index }) => (
                    <TouchableOpacity
                        style={{ flex: 1, paddingHorizontal: 6 }}
                    // onPress={_onPress(item)}
                    >
                        <Text>{item.rtspUrl}</Text>
                        <Image style={{ height: (WIDTH_SCREEN / 2 - 24) / 251 * 130, width: '100%', resizeMode: 'contain' }} source={{ uri: THUMBNAILS }} />
                    </TouchableOpacity>


                )}
            />

        </View>
    );
};

const styles = StyleSheet.create({
    gridView: {
        flex: 1,
    },
    container: {
        flex: 1,
        backgroundColor: Colors.lighter,
        width: '100%'
    },
    body: {
        backgroundColor: Colors.white,
    },
    itemContainer: {
        justifyContent: 'flex-end',
        borderRadius: 5,
        padding: 10,
        height: 150,
    }
});

